import { useState, useEffect } from "react";  // Added useEffect back
import { Html5QrcodeScanner } from "html5-qrcode";  // Import the barcode scanner

export default function App() {
  const [view, setView] = useState("scan");
  const [isbn, setIsbn] = useState("");
  const [manualIsbn, setManualIsbn] = useState("");
  const [titleFromBackend, setTitleFromBackend] = useState("");
  const [manualTitle, setManualTitle] = useState("");
  const [price, setPrice] = useState("");
  const [quantity, setQuantity] = useState("1");
  const [showManualTitle, setShowManualTitle] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [isSaved, setIsSaved] = useState(false);

  const fetchTitle = async (isbnToUse) => {
    try {
      const response = await fetch("https://testocrtest.pythonanywhere.com/receive_isbn", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isbn: isbnToUse }),
      });

      const data = await response.json();
      setIsbn(isbnToUse);

      if (data.title) {
        setTitleFromBackend(data.title);
        setManualTitle("");
        setShowManualTitle(false);
      } else {
        setTitleFromBackend("");
        setShowManualTitle(true);
      }

      setView("priceEntry");
    } catch (error) {
      console.error("‚ùå Fetch error:", error);
      setTitleFromBackend("");
      setShowManualTitle(true);
      setView("priceEntry");
    }
  };

  const sendToBackend = async () => {
    const title = titleFromBackend || manualTitle;
    if (!isbn || !title || !price || !quantity) return;

    try {
      const response = await fetch("https://testocrtest.pythonanywhere.com/save_title", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isbn, b_title: title, price, quantity }),
      });

      const data = await response.json();
      setIsSaved(true);
      setSaveMessage("‚úÖ Saved successfully");
      console.log("‚úÖ Saved:", data);
    } catch (error) {
      console.error("‚ùå Save error:", error);
    }
  };

  const handleManualIsbnFetch = () => {
    const trimmed = manualIsbn.trim();
    if (trimmed) {
      fetchTitle(trimmed);
    }
  };

  const handleBack = () => {
    setView("scan");
    setIsbn("");
    setManualIsbn("");
    setTitleFromBackend("");
    setManualTitle("");
    setPrice("");
    setQuantity("1");
    setShowManualTitle(false);
    setIsSaved(false);
    setSaveMessage("");
  };

  // Barcode scanner setup
  useEffect(() => {
    if (view === "liveScanner") {
      const scanner = new Html5QrcodeScanner("reader", {
        fps: 10, // frames per second
        qrbox: 250, // size of scanning box
      });

      scanner.render((decodedText) => {
        // This is where you can process the decoded barcode
        fetchTitle(decodedText);
      });

      // Cleanup the scanner on component unmount
      return () => {
        scanner.clear();
      };
    }
  }, [view]);

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        {view === "scan" && (
          <>
            <h1 style={styles.header}>üìö ISBN Scanner</h1>
            <p style={styles.subText}>Point your camera at the barcode</p>
            <button style={styles.primaryButton} onClick={() => setView("liveScanner")}>
              üé¶ Start Live Scanner
            </button>
            <button style={styles.manualButton} onClick={() => setView("manualIsbn")}>
              üìÑ Enter ISBN Manually
            </button>
          </>
        )}

        {view === "manualIsbn" && (
          <>
            <h3>Enter ISBN manually</h3>
            <input
              value={manualIsbn}
              onChange={(e) => setManualIsbn(e.target.value)}
              placeholder="Enter ISBN"
              style={styles.input}
            />
            <button style={styles.primaryButton} onClick={handleManualIsbnFetch}>
              üîç Fetch Title
            </button>
            <button style={styles.secondaryButton} onClick={handleBack}>
              üîô Back
            </button>
          </>
        )}

        {view === "liveScanner" && (
          <>
            <h3>üì∑ Live Barcode Scanner</h3>
            <div id="reader" style={{ width: "100%" }}></div>
            <button style={styles.secondaryButton} onClick={handleBack}>
              üîô Return to Scanner
            </button>
          </>
        )}

        {view === "priceEntry" && (
          <>
            <p><strong>ISBN:</strong> {isbn}</p>
            {titleFromBackend && <p><strong>Title:</strong> {titleFromBackend}</p>}

            {showManualTitle && (
              <>
                <p>Enter Book Title:</p>
                <input
                  value={manualTitle}
                  onChange={(e) => setManualTitle(e.target.value)}
                  placeholder="Enter title"
                  style={styles.input}
                />
              </>
            )}

            <p>Enter Price:</p>
            <input
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter price"
              style={styles.input}
            />

            <p>Enter Quantity:</p>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              placeholder="Enter quantity"
              style={styles.input}
            />

            {!isSaved && (
              <button style={styles.saveButton} onClick={sendToBackend}>
                üíæ Save
              </button>
            )}

            {saveMessage && <p style={{ marginTop: 12, color: "green" }}>{saveMessage}</p>}

            <button style={styles.secondaryButton} onClick={handleBack}>
              üîô Return to Scanner
            </button>
          </>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    background: "linear-gradient(to right, #e0f7fa, #fefefe)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "'Segoe UI', sans-serif",
    padding: "20px",
  },
  card: {
    width: "100%",
    maxWidth: "420px",
    background: "#fff",
    padding: "30px",
    borderRadius: "20px",
    boxShadow: "0 15px 35px rgba(0,0,0,0.1)",
    textAlign: "center",
  },
  header: {
    fontSize: "26px",
    color: "#007bff",
  },
  subText: {
    color: "#666",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    width: "90%",
    borderRadius: "8px",
    border: "1px solid #ccc",
    marginBottom: "12px",
  },
  primaryButton: {
    background: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "12px",
    padding: "14px 28px",
    fontSize: "16px",
    cursor: "pointer",
    marginTop: "10px",
  },
  manualButton: {
    background: "#17a2b8",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    padding: "12px 22px",
    fontSize: "15px",
    cursor: "pointer",
    marginTop: "10px",
  },
  saveButton: {
    background: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    padding: "12px 22px",
    fontSize: "16px",
    cursor: "pointer",
    marginTop: "10px",
  },
  secondaryButton: {
    background: "#ffc107",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    padding: "12px 22px",
    fontSize: "15px",
    cursor: "pointer",
    marginTop: "20px",
  },
};
